windowclass.script:
        	function onInit()
				self.onBastionOptionChanged();
			end
			function onBastionOptionChanged()
				local bShow = OptionsManager.isOption("HRBASTION", "on");
				if bShow then
					footer.setValue("charsheet_notes_footer", getDatabaseNode());
				else
					footer.setValue("", "");
				end
				footer.setVisible(bShow);
			end
            function onDrop(x, y, draginfo)
                if draginfo.isType("shortcut") then
                    local sClass, sRecord = draginfo.getShortcutData();
                    if sClass == "ref_adventure" then
                        return CharBuildDropManager.addInfoDB(getDatabaseNode(), sClass, sRecord);
                    end
                end
            end
